import org.apache.nifi.processor.io.InputStreamCallback
import org.apache.nifi.processor.io.OutputStreamCallback
import groovy.json.JsonSlurper
import groovy.json.JsonOutput
import java.nio.charset.StandardCharsets

def toInt(v){ if(v==null) return null; try{ return Integer.valueOf(v.toString()) }catch(e){ return null } }
def toDbl(v){ if(v==null) return null; try{ return Double.valueOf(v.toString()) }catch(e){ return null } }
def sOrEmpty(v){ v==null ? "" : v.toString() }
def sOrNull(v){ v==null ? null : v.toString() }
def slug(s){ (s==null ? "unknown" : s.toLowerCase().replaceAll("\\W+","_")) }

def ff = session.get()
if (!ff) return

try {
    // read one-row JSON from SplitJson
    String rowText = null
    session.read(ff, { inStream -> rowText = new String(inStream.bytes, StandardCharsets.UTF_8) } as InputStreamCallback)
    def row = new JsonSlurper().parseText(rowText) as Map

    // --- parse role info
    def roleInfo = [:]
    if (row.ralis_users_role_info != null) {
        if (row.ralis_users_role_info instanceof Map) {
            roleInfo = (Map) row.ralis_users_role_info
        } else {
            try { roleInfo = (new JsonSlurper().parseText(row.ralis_users_role_info.toString())) as Map }
            catch (ignored) { roleInfo = [:] }
        }
    }

    // --- parse ralis_processes_parameters (flatten first element)
    def p = [:]
    if (row.ralis_processes_parameters != null) {
        def parsed = null
        if (row.ralis_processes_parameters instanceof List) {
            parsed = row.ralis_processes_parameters
        } else {
            try { parsed = (new JsonSlurper().parseText(row.ralis_processes_parameters.toString())) as List }
            catch (ignored) { parsed = [] }
        }
        if (parsed && parsed.size() > 0) {
            p = parsed[0] as Map
        }
    }

    // --- parse ralis_roles_email (flatten)
    def e = [:]
    if (row.ralis_roles_email != null) {
        if (row.ralis_roles_email instanceof Map) {
            e = (Map) row.ralis_roles_email
        } else {
            try { e = (new JsonSlurper().parseText(row.ralis_roles_email.toString())) as Map }
            catch (ignored) { e = [:] }
        }
    }

    // ---------------- application (all flattened here) ----------------
    def app = [
        ralis_application_id     : sOrNull(row.ralis_application_id),
        application_no           : sOrNull(row.application_no),
        status                   : (row.status instanceof String && row.status.contains("|")) ? row.status.split("\\|",2)[0] : sOrNull(row.status),
        date                     : sOrNull(row.date),
        deleted                  : (row.deleted?.toString()?.equalsIgnoreCase("true")),
        naeb_application         : (row.naeb_application?.toString()?.equalsIgnoreCase("true")),
        ralis_users_id           : sOrNull(row.ralis_users_id),
        ralis_users_names        : sOrNull(row.ralis_users_names),
        ralis_users_email        : sOrNull(row.ralis_users_email),
        ralis_users_password     : sOrNull(row.ralis_users_password),
        ralis_users_checkpoint   : sOrNull(row.ralis_users_checkpoint),
        ralis_users_date         : sOrNull(row.ralis_users_date),
        ralis_users_deleted      : (row.ralis_users_deleted?.toString()?.equalsIgnoreCase("true")),
        ralis_categories_id      : sOrNull(row.ralis_categories_id),
        ralis_categories_name    : sOrNull(row.ralis_categories_name),
        ralis_categories_type    : sOrNull(row.ralis_categories_type),
        ralis_categories_deleted : (row.ralis_categories_deleted?.toString()?.equalsIgnoreCase("true")),
        ralis_processes_id       : sOrNull(row.ralis_processes_id),
        ralis_processes_name     : sOrNull(row.ralis_processes_name),
        ralis_processes_deleted  : (row.ralis_processes_deleted?.toString()?.equalsIgnoreCase("true")),
        ralis_roles_id           : sOrNull(row.ralis_roles_id),
        ralis_roles_name         : sOrNull(row.ralis_roles_name),
        ralis_user_phone         : sOrNull(roleInfo.phone),
        ralis_user_id_number     : sOrNull(roleInfo.id_number),
        ralis_user_address       : sOrNull(roleInfo.address),
        ralis_user_tin           : sOrNull(roleInfo.tin),

        // --- flattened ralis_processes_parameters
        ralis_processes_parameters_name     : sOrNull(p.name),
        ralis_processes_parameters_label    : sOrNull(p.label),
        ralis_processes_parameters_type     : sOrNull(p.type),
        ralis_processes_parameters_required : sOrNull(p.required),
        ralis_processes_parameters_options  : sOrNull(p.options),
        ralis_processes_parameters_role     : sOrNull(p.role),

        // --- flattened ralis_roles_email
        ralis_roles_email_from                    : sOrNull(e.from),
        ralis_roles_email_name                    : sOrNull(e.name),
        ralis_roles_email_subject                 : sOrNull(e.subject),
        ralis_roles_email_content                 : sOrNull(e.content),
        ralis_roles_email_application_subject     : sOrNull(e.application_subject),
        ralis_roles_email_application_content     : sOrNull(e.application_content),
        ralis_roles_email_application_process_subject: sOrNull(e.application_process_subject),
        ralis_roles_email_application_process_content: sOrNull(e.application_process_content)
    ]

    // --- write app flowfile
    def appFF = session.create(ff)
    def appBytes = JsonOutput.toJson(app).getBytes(StandardCharsets.UTF_8)
    appFF = session.write(appFF, { out -> out.write(appBytes) } as OutputStreamCallback)
    appFF = session.putAttribute(appFF, "mime.type", "application/json")
    appFF = session.putAttribute(appFF, "route", "app")
    session.transfer(appFF, REL_SUCCESS)

    // ---------------- category_info  CI rows ----------------
    def ciKey = slug(row.ralis_categories_name) + "_" + slug(row.ralis_categories_type)

    def cinfo = [:]
    if (row.category_info != null) {
        if (row.category_info instanceof Map) {
            cinfo = (Map) row.category_info
        } else {
            try { cinfo = (new JsonSlurper().parseText(row.category_info.toString())) as Map }
            catch (ignored) { cinfo = [:] }
        }
    }

    def hscode = sOrNull(cinfo.hscode)
    def unitFromGeneral = sOrNull(cinfo.quantity_unit)

    // A) items style
    if (cinfo.items instanceof List && !cinfo.items.isEmpty()) {
        cinfo.items.eachWithIndex { item, i ->
            def r = [
                application_id      : sOrNull(row.ralis_application_id),
                category_key        : ciKey,
                idx                 : toInt(i + 1),
                hscode              : sOrNull(hscode),
                description         : sOrEmpty(item.description),
                quantity            : toDbl(item.quantity),
                quantity_units      : sOrEmpty(item.quantity_units ?: unitFromGeneral),
                plant_name          : sOrEmpty(item.plant_name),
                botanical_name      : sOrEmpty(item.botanical_name),
                production_location : sOrEmpty(item.production_location),
                inputs_used         : sOrEmpty(item.inputs_used),
                ci_text             : JsonOutput.toJson(item)
            ]
            def ciFF = session.create(ff)
            def ciBytes = JsonOutput.toJson(r).getBytes(StandardCharsets.UTF_8)
            ciFF = session.write(ciFF, { out -> out.write(ciBytes) } as OutputStreamCallback)
            ciFF = session.putAttribute(ciFF, "mime.type", "application/json")
            ciFF = session.putAttribute(ciFF, "route", "ci")
            session.transfer(ciFF, REL_SUCCESS)
        }
    } else {
        // B) legacy parallel arrays
        def pn = (cinfo.plant_name ?: []) as List
        def bt = (cinfo.botanical_name ?: []) as List
        def ds = (cinfo.description ?: []) as List
        def qt = (cinfo.quantity ?: []) as List
        def qu = (cinfo.quantity_units ?: []) as List
        def pl = (cinfo.production_location ?: []) as List
        def iu = (cinfo.inputs_used ?: []) as List
        int n = [pn.size(), bt.size(), ds.size(), qt.size(), qu.size(), pl.size(), iu.size(), 0].max()

        (0..<n).each { i ->
            def r = [
                application_id      : sOrNull(row.ralis_application_id),
                category_key        : ciKey,
                idx                 : toInt(i + 1),
                hscode              : sOrNull(hscode),
                description         : sOrEmpty(i < ds.size() ? ds[i] : null),
                quantity            : toDbl(i < qt.size() ? qt[i] : null),
                quantity_units      : sOrEmpty((i < qu.size() ? qu[i] : null) ?: unitFromGeneral),
                plant_name          : sOrEmpty(i < pn.size() ? pn[i] : null),
                botanical_name      : sOrEmpty(i < bt.size() ? bt[i] : null),
                production_location : sOrEmpty(i < pl.size() ? pl[i] : null),
                inputs_used         : sOrEmpty(i < iu.size() ? iu[i] : null),
                ci_text             : JsonOutput.toJson([
                    plant_name: (i < pn.size() ? pn[i] : null),
                    botanical_name: (i < bt.size() ? bt[i] : null),
                    description: (i < ds.size() ? ds[i] : null),
                    quantity: (i < qt.size() ? qt[i] : null),
                    quantity_units: (i < qu.size() ? qu[i] : null),
                    production_location: (i < pl.size() ? pl[i] : null),
                    inputs_used: (i < iu.size() ? iu[i] : null)
                ])
            ]
            def ciFF = session.create(ff)
            def ciBytes = JsonOutput.toJson(r).getBytes(StandardCharsets.UTF_8)
            ciFF = session.write(ciFF, { out -> out.write(ciBytes) } as OutputStreamCallback)
            ciFF = session.putAttribute(ciFF, "mime.type", "application/json")
            ciFF = session.putAttribute(ciFF, "route", "ci")
            session.transfer(ciFF, REL_SUCCESS)
        }
    }

    // drop original row
    session.remove(ff)

} catch (e) {
    log.error('ExecuteGroovyScript error', e)
    session.transfer(ff, REL_FAILURE)
}
